mapper.select:
select ifnull((select Max(bwp_id)
					 from bwpage
					 where bwp_id < #{bwp_id}
					 AND bw_id = #{bw_id}),Max(bwp_id)) from bwpage where bw_id = #{bw_id}


select bwi_content
from bwidea
where bwp_id= #{bwp_id}

select bw_pagenum
from bw
where bw_id = #{bw_id}

select bw_timer
from bw
where bw_id = #{bw_id}

select bw_id,bw_sub,bw_time
from bw
where t_id = #{t_id}
